function parallax(){document.addEventListener("mousemove",e=>{var t=window.innerWidth/2,n=window.innerHeight/2,t=120*((e.clientX-t)/t),e=45*((e.clientY-n)/n);document.querySelector(".card").style.setProperty("--rotatex",-1*e+"deg"),document.querySelector(".card").style.setProperty("--rotatey",t+"deg")})}function parallaxMobile(){window.DeviceOrientationEvent&&window.addEventListener("deviceorientation",e=>{var t=e.beta-40,e=e.gamma;document.querySelector(".card").style.setProperty("--rotatex",-1*t+"deg"),document.querySelector(".card").style.setProperty("--rotatey",e+"deg")},!0)}function setPanel(t,e){var n=document.querySelectorAll(".banner__panel");"initial"==e?n.forEach(e=>{e.setAttribute("data-content",t)}):n[e].setAttribute("data-content",t)}function changeCounter(){const e=document.querySelectorAll(".banner__panel");document.getElementById("counter-btn").addEventListener("click",()=>{document.getElementById("banner").style.animation="rotatePanel 2s forwards",e.forEach((e,t)=>{setPanel("321d : 10h : 34m : 22s",t)})})}document.addEventListener("DOMContentLoaded",()=>{parallax(),parallaxMobile()});