function parallax(){document.addEventListener("mousemove",e=>{var t=window.innerWidth/2,n=window.innerHeight/2,t=20*((e.clientX-t)/t),e=20*((e.clientY-n)/n);document.querySelector(".card").style.setProperty("--rotatex",-1*e+"deg"),document.querySelector(".card").style.setProperty("--rotatey",t+"deg")})}function setPanel(t,e){var n=document.querySelectorAll(".banner__panel");"initial"==e?n.forEach(e=>{e.setAttribute("data-content",t)}):n[e].setAttribute("data-content",t)}function changeCounter(){const e=document.querySelectorAll(".banner__panel");document.getElementById("counter-btn").addEventListener("click",()=>{e.forEach((e,t)=>{e.classList.add("banner--change"),e.addEventListener("animationend",()=>{setPanel("321d : 10h : 34m : 22s",t),e.classList.remove("banner--change")})})})}document.addEventListener("DOMContentLoaded",()=>{parallax();setPanel("123d : 23h : 56m : 11s","initial"),changeCounter()});